import React from "react";
import { Link, useLocation } from "react-router-dom";

const Cart = ({ cartItems, totalPrice, removeFromCart }) => {
  const location = useLocation();

  if (cartItems.length === 0) {
    return null; // Ïû•Î∞îÍµ¨ÎãàÍ∞Ä ÎπÑÏñ¥ÏûàÏúºÎ©¥ ÏïÑÎ¨¥Í≤ÉÎèÑ Î†åÎçîÎßÅÌïòÏßÄ ÏïäÏùå
  }

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("ko-KR", {
      style: "currency",
      currency: "KRW",
    })
      .format(amount)
      .replace("‚Ç©", "")
      .trim(); // '‚Ç©' Ï†úÍ±∞
  };

  return (
    <div className="cart">
      <h2>Ïû•Î∞îÍµ¨Îãà</h2>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>
            <span className="item-name">{item.title}</span>
            <span className="item-price">{formatCurrency(item.price)}Ïõê</span>
            <button
              onClick={() => removeFromCart(item.id)}
              className="remove-button"
            >
              üóëÔ∏è
            </button>
          </li>
        ))}
      </ul>
      <p className="total-price">Ï¥ù Í∏àÏï°: {formatCurrency(totalPrice)}Ïõê</p>
      {location.pathname !== "/checkout" && (
        <Link to="/checkout">
          <button className="checkout-button">Í≤∞Ï†úÌïòÍ∏∞</button>
        </Link>
      )}
    </div>
  );
};

export default Cart;
