// src/actions/cartActions.js

// 장바구니에 게임 추가
export const addToCart = (game) => {
  return {
    type: 'ADD_TO_CART',
    payload: game,
  };
};

// 장바구니에서 게임 제거
export const removeFromCart = (gameId) => {
  return {
    type: 'REMOVE_FROM_CART',
    payload: gameId,
  };
};
