// Game_Detail.js
import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { addToCart } from '../redux/cartActions'; // Ïï°ÏÖò ÏûÑÌè¨Ìä∏
import Slide from './Game_Detail_Slide';
import Footer from '../components/Game_Detail_Footer';
import gamesData from '../gamesData';
import '../css/GameDetail.css';

const Game_Detail = () => {
  const { id } = useParams();
  const game = gamesData.find((g) => g.id === parseInt(id));
  const dispatch = useDispatch();  // dispatchÎ•º ÏÇ¨Ïö©ÌïòÏó¨ Redux ÏÉÅÌÉú Î≥ÄÍ≤Ω

  // cartItemsÍ∞Ä undefinedÏùº Í≤ΩÏö∞ Îπà Î∞∞Ïó¥Î°ú Ï≤òÎ¶¨
  const cartItems = useSelector((state) => state.cart?.cartItems || []);  

  const navigate = useNavigate(); // navigate ÌõÖ ÏÇ¨Ïö©

  if (!game) {
    return <h2>Í≤åÏûÑÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</h2>;
  }

  // Í≤åÏûÑÏù¥ Ïû•Î∞îÍµ¨ÎãàÏóê ÏûàÎäîÏßÄ ÌôïÏù∏
  const isInCart = cartItems.some(item => item.id === game.id);

  // Ïû•Î∞îÍµ¨ÎãàÏóê Í≤åÏûÑ Ï∂îÍ∞Ä
  const handleAddToCart = () => {
    dispatch(addToCart(game)); // addToCart Ïï°ÏÖò Ìò∏Ï∂ú
  };

  return (
    <div className="scroll">
      <div id="wrapper_header">
        <hr className='game_detail_hr' />
        <h1 id="game_title">{game.title}</h1>
      </div>
      <div>
        <Slide />
        <div id="wrapper">
          <pre className="game_description">
            {game.description}
          </pre>

          <hr className='game_detail_hr' />

          <h4 className="game_review">&nbsp;&nbsp;&nbsp;üéÆ Ïû•Î•¥ : {game.genre}</h4>
          <h4 className="game_review">&nbsp;&nbsp;&nbsp;üëç ÌèâÍ∞Ä : {game.review}</h4>
          <h4 className="game_review">&nbsp;&nbsp;&nbsp;üìÜ Ï∂úÏãúÏùº : {game.release}</h4>
          <h4 className="game_review">&nbsp;&nbsp;&nbsp;üßë‚Äçüíª Í∞úÎ∞úÏÇ¨ : {game.developer}</h4>

          <hr className='game_detail_hr' />

          <div className="eval_option">
            {/* <p className="eval_p">Ï¢ÖÌï©ÌèâÍ∞Ä:</p>
            <h3 className="eval_h3">{game.review}</h3> */}
          </div>
          <Footer 
            addToCart={handleAddToCart} 
            game={game} 
            isInCart={isInCart} 
            navigate={navigate} 
          />
          <br />
        </div>
      </div>
    </div>
  );
}

export default Game_Detail;
