import React from 'react';

const Footer = ({ addToCart, game, isInCart, navigate }) => {
  return (
    <div className="footer">
      {isInCart ? (
        <button disabled>장바구니에 이미 있습니다</button>
      ) : (
        <button onClick={() => addToCart(game)}>장바구니에 추가</button>
      )}
    </div>
  );
};

export default Footer;
