// Game_Detail_Footer.js
import React from 'react';
import '../css/Game_Detail_Footer.css';

const Game_Detail_Footer = ({ addToCart, game, isInCart, navigate }) => {
  return (
    <div className="Footer">
      <button
        onClick={() => addToCart(game)}
        disabled={isInCart}
				className="addbtn"
				style={{width: "64px", height: "64px"}}
      >
        {isInCart ? '해당 게임은 이미 추가되어있습니다.' : '장바구니에 추가'}
      </button>
      <button onClick={() => navigate(-1)} className="backbtn">
        뒤로가기
      </button>
    </div>
  );
};

export default Game_Detail_Footer;
