// src/components/ReactLogo.js
import React from 'react';
import { Link } from 'react-router-dom'; // Link를 import 합니다.
import '../css/Header.css'; // 필요에 따라 CSS 파일을 추가할 수 있습니다.

const SteamLogo = () => {
  return (
    <Link to="/"> {/* Link로 감싸줍니다. */}
      <div className="Steam-logo">
        <img 
          src="https://store.fastly.steamstatic.com/public/shared/images/responsive/header_logo.png" 
          alt="Steam Logo" 
          className="steam-logo" 
        />
      </div>
    </Link>
  );
};

export default ReactLogo;
