// src/components/Header.js
import React, { useState } from 'react';
import SteamLogo from './SteamLogo';

const Header = () => {
  const [showCategories, setShowCategories] = useState(false);

  const toggleCategories = () => {
    setShowCategories(!showCategories);
  };

  const handleCategoryClick = (category) => {
    // 카테고리에 따라 페이지 이동 로직 구현
    alert(`${category} 카테고리로 이동합니다.`); // 페이지 이동 대신 alert로 대체
  };

  return (
    <header>
      <SteamLogo />
      <h1>나의 게임 사이트</h1>
      <button onClick={toggleCategories}>
        {showCategories ? '-' : '+'} 카테고리
      </button>
      {showCategories && (
        <div className="category-list">
          <button onClick={() => handleCategoryClick('Action')}>Action</button>
          <button onClick={() => handleCategoryClick('Fear')}>Fear</button>
          <button onClick={() => handleCategoryClick('Puzzle')}>Puzzle</button>
          <button onClick={() => handleCategoryClick('RPG')}>RPG</button>
          <button onClick={() => handleCategoryClick('Sports')}>Sports</button>
          <button onClick={() => handleCategoryClick('Simulation')}>Simulation</button>
          <button onClick={() => handleCategoryClick('FPS')}>FPS</button>
        </div>
      )}
    </header>
  );
};

export default Header;
