// App.js
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart, removeFromCart } from './redux/cartActions'; // addToCart, removeFromCart 액션 임포트
import { Routes, Route } from 'react-router-dom';
import './App.css';
import Game_Detail from './pages/Game_Detail';
import Home from './pages/Home';
import CartPage from './pages/CartPage';
import gamesData from './gamesData';

const App = () => {
  const dispatch = useDispatch();

  // Redux에서 cartItems를 가져옴
  const cartItems = useSelector((state) => state.cart.cartItems);

  const addGameToCart = (game) => {
    dispatch(addToCart(game));  // dispatch를 통해 cart에 게임 추가
  };

  const removeGameFromCart = (gameId) => {
    dispatch(removeFromCart(gameId));  // dispatch를 통해 cart에서 게임 제거
  };

  return (
    <Routes>
      <Route path="/" element={<Home gamesData={gamesData} addToCart={addGameToCart} />} />
      <Route
        path="/game/:id"
        element={<Game_Detail addToCart={addGameToCart} cartItems={cartItems} />}
      />
      <Route
        path="/cart"
        element={<CartPage cartItems={cartItems} removeFromCart={removeGameFromCart} />}
      />
    </Routes>
  );
};

export default App;
