const images = [
  '/public/assets/images/game1.jpg',
  '/assets/images/game2.jpg',
  '/assets/images/game3.jpg',
];

const games = [
  {
    id: 1,
    name: 'Game 1',
    description: 'Description of Game 1',
    image: images[0], // 배열에서 첫 번째 이미지 경로 사용
  },
  {
    id: 2,
    name: 'Game 2',
    description: 'Description of Game 2',
    image: images[1], // 배열에서 두 번째 이미지 경로 사용
  },
  {
    id: 3,
    name: 'Game 3',
    description: 'Description of Game 3',
    image: images[2], // 배열에서 세 번째 이미지 경로 사용
  },
];

export default games;
