import React, { useState } from 'react';
import SteamLogo from './SteamLogo';
import { Link } from 'react-router-dom';

const categories = [
  { name: 'Action', path: '/action' },
  { name: 'FPS', path: '/fps' },
  { name: 'RPG', path: '/rpg' },
];

const Header = () => {
  const [showCategories, setShowCategories] = useState(false);

  return (
    <header style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '10px', background: '#222' }}>
      <SteamLogo />
      <button 
        style={{ background: 'transparent', border: 'none', color: 'white', cursor: 'pointer' }} 
        onClick={() => setShowCategories(!showCategories)}
      >
        카테고리
      </button>
      {showCategories && (
        <nav>
          <ul style={{ display: 'flex', listStyleType: 'none', padding: 0, margin: 0 }}>
            {categories.map((category, index) => (
              <li key={index} style={{ margin: '0 10px', color: 'white' }}>
                <Link to={category.path} style={{ color: 'white', textDecoration: 'none' }}>
                  {category.name} ()
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      )}
    </header>
  );
};

export default Header;
