// App.js
import React, { useEffect } from 'react';
import Header from './components/Header.js'; // 헤더 컴포넌트 임포트
import games from './gamesData'; // 게임 데이터 임포트
import './css/Cards.css'; // 카드 스타일 임포트

const App = () => {
	useEffect(() => {
			const steamCards = document.querySelector('.js-steamCards');

			// 게임 배열을 순회하며 카드 생성
			games.forEach((game) => {
					steamCards.insertAdjacentHTML('beforeend', `
							<div class="d-steam-card-wrapper">
									<div class="d-steam-card js-steamCard" style="background-image: url('${game.image}')">
											<div class="card-content">
													<h2>${game.name}</h2>
													<p>${game.description}</p>
													<p>${game.price}</p>
											</div>
									</div>
							</div>
					`);
			});
	}, []);

	return (
			<div>
					<Header /> {/* 헤더 추가 */}
					<div className="d-steam-cards js-steamCards">
							{/* 카드가 생성되는 곳 */}
					</div>
			</div>
	);
};

export default App;
