// App.js
import React, { useEffect } from 'react';
import Header from './Header'; // 헤더 컴포넌트 임포트
import './cards.css'; // 카드 스타일 임포트

const App = () => {
    useEffect(() => {
        const steamCards = document.querySelector('.js-steamCards');
        const listImages = [
            "https://i.postimg.cc/C5NMN512/steam-card-2.jpg",
            "https://i.postimg.cc/prxXzz28/steam-card-1.jpg",
            // ... 나머지 이미지 링크
        ];

        listImages.forEach((image) => {
            steamCards.insertAdjacentHTML('beforeend', `
                <div class="d-steam-card-wrapper">
                    <div class="d-steam-card js-steamCard" style="background-image: url('${image}')"></div>
                </div>
            `);
        });
    }, []);

    return (
        <div>
            <Header /> {/* 헤더 추가 */}
            <div className="d-steam-cards js-steamCards">
                {/* 카드가 생성되는 곳 */}
            </div>
        </div>
    );
};

export default App;
