// src/components/SteamLogo.js
import React from 'react';
import { ReactComponent as SteamIcon } from '../assets/steam.svg'; // steam.svg 파일을 assets 폴더에 저장했다고 가정

const SteamLogo = () => {
  return (
    <div className="social-icon w-gradient">
      <div className="si-circle">
        <svg width="100%" height="100%">
          <circle cx="50%" cy="50%" r="50%" fill="#616161" />
        </svg>
      </div>
      <div className="si-splash">
        <svg width="100%" height="100%">
          {/* 스팀 아이콘의 스플래시 SVG 코드 */}
        </svg>
      </div>
      <div className="si-icon">
        <SteamIcon />
      </div>
      <div className="si-gradient">
        <svg width="100%" height="100%">
          <linearGradient id="gradient1" gradientTransform="rotate(90)">
            <stop offset="0%" stopColor="#FBBC6E" />
            <stop offset="50%" stopColor="#D8456A" />
            <stop offset="100%" stopColor="#4459A9" />
          </linearGradient>
          <circle cx="50%" cy="50%" r="50%" fill="url(#gradient1)" />
        </svg>
      </div>
    </div>
  );
};

export default SteamLogo;
