import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CartIcon from './CartIcon';
import SearchBar from './SearchBar';

function Header() {
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString());
    }, 1000);

    // Cleanup interval on component unmount
    return () => clearInterval(timer);
  }, []);

  return (
    <header>
      <div className="logo">
        <Link to="/">
          <img src="https://store.fastly.steamstatic.com/public/shared/images/header/logo_steam.svg?t=962016" width="176" height="44" alt="Steam 홈페이지 링크" />
        </Link>
        <span className="hologram">Steam</span>
      </div>
      <SearchBar />
      <div className="separator"></div> {/* 점선 구분자 추가 */}
      <div className="user-info">
        <p>현재 시간: {currentTime}</p>
        <CartIcon />
      </div>
    </header>
  );
}

export default Header;
