// src/gamesData.js
const games = Array.from({ length: 50 }, (_, index) => ({
  id: index + 1,
  name: `Game ${index + 1}`,
  description: `Description of Game ${index + 1}`,
  image: '/assets/images/game1.jpg' // 동일한 이미지를 사용하지만 실제로는 각각 다른 이미지를 사용할 수 있습니다.
}));

export default games;
