import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Card from './components/Card';
import Cart from './components/Cart';
import Game_Detail from './components/Game_Detail';
import Header from './components/Header';
import Footer from './components/Footer';
import Checkout from './components/CheckOut';
import gamesData from './gamesData';
import './App.css';

// App 컴포넌트
const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  const addToCart = (game) => {
    setCartItems([...cartItems, game]);
    setTotalPrice(totalPrice + game.price);
  };

  const removeFromCart = (gameId) => {
    const updatedCart = cartItems.filter((item) => item.id !== gameId);
    const removedGame = cartItems.find((item) => item.id === gameId);
    setCartItems(updatedCart);
    setTotalPrice(totalPrice - removedGame.price);
  };

  return (
    <Router>
      <Header />
      <div className="main-container">
				<div className="content">

          <Routes>
            {/* 홈 화면 */}
            <Route path="/" element={
              <div className="d-steam-cards">
                {gamesData.map((game, index) => (
                  <Card key={index} game={game} addToCart={addToCart} />
                ))}
              </div>
            } />

            {/* 게임 상세 화면 */}
            <Route path="/game/:id" element={
              <Game_Detail addToCart={addToCart} cartItems={cartItems} />
            } />

            {/* 결제 화면 */}
            <Route path="/checkout" element={<Checkout />} />

            {/* 카테고리별 게임 리스트 */}
            <Route path="/action" element={<CardList category="액션" addToCart={addToCart} />} />
            <Route path="/fps" element={<CardList category="FPS" addToCart={addToCart} />} />
            <Route path="/rpg" element={<CardList category="RPG" addToCart={addToCart} />} />
            <Route path="/fear" element={<CardList category="공포" addToCart={addToCart} />} />
            <Route path="/sports" element={<CardList category="스포츠" addToCart={addToCart} />} />
            <Route path="/simulation" element={<CardList category="시뮬레이션" addToCart={addToCart} />} />
            <Route path="/puzzle" element={<CardList category="퍼즐" addToCart={addToCart} />} />
          </Routes>
        </div>

        {/* 장바구니 사이드바 표시 */}
        {cartItems.length > 0 && (
          <div className="sidebar">
            <Cart cartItems={cartItems} totalPrice={totalPrice} removeFromCart={removeFromCart} />
          </div>
        )}
      </div>
      <Footer />
    </Router>
  );
};

// 카테고리별 게임 리스트 컴포넌트
const CardList = ({ category, addToCart }) => {
  const filteredGames = gamesData.filter((game) => game.genre === category);

  return (
    <div>
      <h2 className='categoryGameName'>카테고리: {category}</h2>
      <div className="d-steam-cards">
        {filteredGames.map((game, index) => (
          <Card key={index} game={game} addToCart={addToCart} />
        ))}
      </div>
    </div>
  );
};

export default App;
