// src/components/Header.js
import React, { useEffect } from 'react';
import SteamLogo from './SteamLogo';
import './Header.css'; // CSS 파일을 불러옵니다.

const Header = () => {
  useEffect(() => {
    // 글리치 윈도우를 추가하고, 기존 h1을 클론하여 추가합니다.
    const glitchWindow = document.createElement('div');
    glitchWindow.className = 'glitch-window';
    const titleElement = document.querySelector('h1.glitched');

    if (titleElement) {
      const clonedTitle = titleElement.cloneNode(true);
      glitchWindow.appendChild(clonedTitle);
      document.querySelector('header').appendChild(glitchWindow);
    }
  }, []);

  return (
    <header className="header">
      <SteamLogo />
      <h1 className="glitched">나의 게임 사이트</h1>
    </header>
  );
};

export default Header;
