import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Header from "./components/Header";
import Footer from "./components/Footer";
import HomePage from "./pages/HomePage";
import GamePage from "./pages/GamePage";
import CategoryList from "./components/CategoryList";
import "./App.css";

function App() {
  return (
    <Router>
      <div className="App">
        <Header />
        <div className="banner">
          <video 
            loop 
            muted 
            playsInline 
            poster="https://shared.fastly.steamstatic.com/store_item_assets/steam/clusters/frontpage/b5bc1b1fffa9a7369b92e1a3/page_bg_koreana.jpg?t=1730147890" 
            id="home_video_desktop" 
            className="fullscreen-bg__video" 
            alt="특집 프로모션"
          >
            <source src="https://shared.fastly.steamstatic.com/store_item_assets/steam/clusters/frontpage/b5bc1b1fffa9a7369b92e1a3/webm_page_bg_koreana.webm?t=1730147890" type="video/webm" />
            <source src="https://shared.fastly.steamstatic.com/store_item_assets/steam/clusters/frontpage/b5bc1b1fffa9a7369b92e1a3/mp4_page_bg_koreana.mp4?t=1730147890" type="video/mp4" />
            비디오를 재생할 수 없습니다. 
          </video>
        <div className="wrapper">
          <CategoryList />
          <div className="center-panel">
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/game/:id" element={<GamePage />} />
            </Routes>
          </div>
					</div>
					</div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
