// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Card from './components/Card';
import Header from './components/Header';
import Footer from './components/Footer';
import gamesData from './gamesData';

const App = () => {
  return (
    <Router>
      <div>
        <Header />
        <div className="d-steam-cards">
          <Routes>
            <Route path="/action" element={<CardList category="Action" />} />
            <Route path="/fear" element={<CardList category="Fear" />} />
            <Route path="/puzzle" element={<CardList category="Puzzle" />} />
            <Route path="/rpg" element={<CardList category="RPG" />} />
            <Route path="/sports" element={<CardList category="Sports" />} />
            <Route path="/simulation" element={<CardList category="Simulation" />} />
            <Route path="/fps" element={<CardList category="FPS" />} />
            {/* 기본 경로 추가 */}
            <Route path="/" element={
              gamesData.map((game, index) => (
                <Card key={index} title={game.title} imageUrl={game.imageUrl} />
              ))
            } />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
};

// 카드 리스트 컴포넌트 (예시로 간단히 구현)
const CardList = ({ category }) => {
  return (
    <div>
      <h2>{category} Games</h2>
      {/* 여기에서 카테고리별 게임을 표시하는 코드를 작성 */}
    </div>
  );
};

export default App;
