// src/reducers/cartReducer.js

const initialState = {
  cartItems: [],
  totalPrice: 0,
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      return {
        ...state,
        cartItems: [...state.cartItems, action.payload],
        totalPrice: state.totalPrice + action.payload.price,
      };
    case 'REMOVE_FROM_CART':
      const updatedCart = state.cartItems.filter(item => item.id !== action.payload);
      const removedGame = state.cartItems.find(item => item.id === action.payload);
      return {
        ...state,
        cartItems: updatedCart,
        totalPrice: state.totalPrice - removedGame.price,
      };
    default:
      return state;
  }
};

export default cartReducer;  // 기본 내보내기
