import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import gamesData from '../gamesData';
import './GameDetail.css'; // CSS 파일 import

const GameDetail = ({ addToCart, cartItems }) => {
    const { id } = useParams();
    const game = gamesData.find(g => g.id === parseInt(id));
    const navigate = useNavigate(); // navigate 훅 사용

    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('ko-KR', {
            style: 'currency',
            currency: 'KRW',
        }).format(amount).replace('₩', '').trim(); // '₩' 제거
    };

    const isInCart = game && cartItems.some(item => item.id === game.id);
    const [added, setAdded] = useState(isInCart);

    useEffect(() => {
        if (game) {
            setAdded(cartItems.some(item => item.id === game.id));
        }
    }, [cartItems, game]);

    if (!game) {
        return (
            <div className="game-detail">
                <h2>게임을 찾을 수 없습니다.</h2>
                <button onClick={() => navigate(-1)} className="back-button">뒤로가기</button>
            </div>
        );
    }

    const handleAddToCart = () => {
        addToCart(game);
        setAdded(true);
    };

    return (
        <div className="game-detail">
            <h2>{game.name}</h2>
            <img src={game.imageUrl} alt={game.name} className="game-image" />
            <p><strong>장르:</strong> {game.genre}</p>
            <p><strong>개발자:</strong> {game.developer}</p>
            <p><strong>평가:</strong> {game.review}</p>
            <p><strong>연령 등급:</strong> {game.agerating}</p>
            <p><strong>출시일:</strong> {game.release}</p>
            <p><strong>가격:</strong> {formatCurrency(game.price)}원</p>
            <p><strong>최소 사양:</strong> {game.minimum}</p>
            <p><strong>권장 사양:</strong> {game.recommended}</p>
            <p>{game.description}</p>
            <div className="button-container">
                <button onClick={handleAddToCart} disabled={added} className="action-button">
                    {added ? '해당 게임은 이미 추가되어있습니다.' : '장바구니에 추가'}
                </button>
                <button onClick={() => navigate(-1)} className="back-button">뒤로가기</button>
            </div>
        </div>
    );
};

export default GameDetail;
