import React from 'react';
import { Link } from 'react-router-dom';
import '../css/Cards.css';

const Card = ({ game, addToCart }) => {
  // imageUrl을 명시적으로 선언하고, 없을 경우 기본값을 설정할 수도 있습니다.
  const { title, imageUrl } = game;
  const validImageUrl = imageUrl || "https://via.placeholder.com/150"; // 기본 이미지를 설정

  return (
    <div className="d-steam-card-wrapper">
      <Link to={`/game/${game.id}`}>
        <div className="d-steam-card" style={{ backgroundImage: `url(${validImageUrl})` }}>
          <div className="card-title">{title}</div>
        </div>
      </Link>
    </div>
  );
};

export default Card;
