// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Card from './components/Card';
import Header from './components/Header';
import Footer from './components/Footer';
import Cart from './components/Cart';
import GameDetail from './components/GameDetail';
import gamesData from './gamesData';
import './App.css'; // 스타일을 위한 CSS 파일 import

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  // 장바구니에 게임 추가
  const addToCart = (game) => {
    setCartItems([...cartItems, game]);
    setTotalPrice(totalPrice + game.price);
  };

  // 장바구니에서 게임 제거
  const removeFromCart = (gameId) => {
    const updatedCart = cartItems.filter((item) => item.id !== gameId);
    const removedGame = cartItems.find((item) => item.id === gameId);
    setCartItems(updatedCart);
    setTotalPrice(totalPrice - removedGame.price);
  };

  return (
    <Router>
      <Header />
      <div className="main-container">
        <div className="content">
          <Routes>
            <Route path="/" element={
              <div className="d-steam-cards">
                {gamesData.map((game, index) => (
                  <Card 
                    key={index} 
                    title={game.title} 
                    imageUrl={game.imageUrl} 
                    game={game} 
                    addToCart={addToCart} 
                  />
                ))}
              </div>
            } />
            <Route path="/game/:id" element={
              <GameDetail addToCart={addToCart} cartItems={cartItems} />
            } />
            {/* 기존 카테고리 경로 유지 */}
            <Route path="/action" element={<CardList category="Action" />} />
            <Route path="/fear" element={<CardList category="Fear" />} />
            <Route path="/puzzle" element={<CardList category="Puzzle" />} />
            <Route path="/rpg" element={<CardList category="RPG" />} />
            <Route path="/sports" element={<CardList category="Sports" />} />
            <Route path="/simulation" element={<CardList category="Simulation" />} />
            <Route path="/fps" element={<CardList category="FPS" />} />
          </Routes>
        </div>
        {cartItems.length > 0 && (
          <div className="sidebar">
            <Cart cartItems={cartItems} totalPrice={totalPrice} removeFromCart={removeFromCart} />
          </div>
        )}
      </div>
      <Footer />
    </Router>
  );
};

// 카드 리스트 컴포넌트
const CardList = ({ category }) => {
  return (
    <div>
      <h2>{category} Games</h2>
      {/* 여기에 카테고리별 게임 목록 렌더링 로직 추가 */}
    </div>
  );
};

export default App;
