// src/App.js

import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { Provider } from 'react-redux';
import store from './store'; // store import
import Card from './components/Card';
import Header from './components/Header';
import Footer from './components/Footer';
import Cart from './components/Cart';
import Game_Detail from './components/Game_Detail';
import Checkout from './components/CheckOut';
import gamesData from './gamesData';
import './css/Cart.css';
import './App.css';

const App = () => {
  return (
    <Provider store={store}>  {/* 리덕스 Provider로 감싸기 */}
      <Router>
        <Header />
        <div className="main-container">
          <div className="content">
            <Routes>
              <Route path="/" element={
                <div className="d-steam-cards">
                  {gamesData.map((game, index) => (
                    <Card key={index} game={game} />
                  ))}
                </div>
              } />

              <Route path="/game/:id" element={<Game_Detail />} />
              <Route path="/checkout" element={<Checkout />} />
            </Routes>
          </div>
          <Footer />
        </div>
      </Router>
    </Provider>
  );
};

export default App;
