import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Card from './components/Card';
import Cart from './components/Cart';
import GameDetail from './components/GameDetail';
import gamesData from './gamesData';
import './App.css';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  const addToCart = (game) => {
    setCartItems([...cartItems, game]);
    setTotalPrice(totalPrice + game.price);
  };

  const removeFromCart = (gameId) => {
    const updatedCart = cartItems.filter((item) => item.id !== gameId);
    const removedGame = cartItems.find((item) => item.id === gameId);
    setCartItems(updatedCart);
    setTotalPrice(totalPrice - removedGame.price);
  };

  return (
    <Router>
      <Header />
      <div className="main-container">
        <div className="content">
          <Routes>
            <Route path="/" element={
              <div className="d-steam-cards">
                {gamesData.map((game, index) => (
                  <Card key={index} title={game.title} imageUrl={game.imageUrl} game={game} />
                ))}
              </div>
            } />
            <Route path="/game/:id" element={
              <GameDetail addToCart={addToCart} cartItems={cartItems} />
            } />
          </Routes>
        </div>
        {cartItems.length > 0 && ( // 조건부 렌더링 추가
          <div className="sidebar">
            <Cart cartItems={cartItems} totalPrice={totalPrice} removeFromCart={removeFromCart} />
          </div>
        )}
      </div>
      <Footer />
    </Router>
  );
};

export default App;
