import React from 'react';
import { Link } from 'react-router-dom';
import { FaShoppingCart } from 'react-icons/fa'; // react-icons 패키지를 사용해 아이콘 추가

function CartIcon() {
  return (
    <div className="cart-icon">
      <Link to="/cart">
        <span style={{fontSize: "50px"}}>장바구니</span>
        <FaShoppingCart size={30} color="#c7d5e0" />
      </Link>
    </div>
  );
}

export default CartIcon;
