// src/components/Header.js
import React, { useState } from 'react';
import SteamLogo from './SteamLogo';

const Header = () => {
  const [showCategories, setShowCategories] = useState(false);

  const categories = [
    { name: 'Action', link: '/action' },
    { name: 'Fear', link: '/fear' },
    { name: 'Puzzle', link: '/puzzle' },
    { name: 'RPG', link: '/rpg' },
    { name: 'Sports', link: '/sports' },
    { name: 'Simulation', link: '/simulation' },
    { name: 'FPS', link: '/fps' },
  ];

  const handleCategoryClick = (link) => {
    // 해당 링크로 이동 (이 부분은 라우터를 사용하여 구현해야 함)
    window.location.href = link;
  };

  return (
    <header>
      <SteamLogo />
      <h1>나의 게임 사이트</h1>
      <button onClick={() => setShowCategories(!showCategories)}>+</button>
      {showCategories && (
        <div className="category-list">
          {categories.map((category) => (
            <button key={category.name} onClick={() => handleCategoryClick(category.link)}>
              {category.name}
            </button>
          ))}
        </div>
      )}
    </header>
  );
};

export default Header;
